// Navigate to the website
cy.visit('http://automationexercise.com');
// Click login button
cy.get('a[href="/login"]').click();
// Log in with the created user
cy.fixture('user_credentials.json').then(function (credentials) {
    loginPage.login(credentials.email, credentials.password);
});
homePage.verifyLoggedInAs(user.firstName, { timeout: 15000 });
// Add products to cart
productsPage.addFirstProductToCart();
cy.get('.modal-content').should('be.visible');
cy.get('.modal-title').should('contain.text', 'Added!');
cy.get('button.close-modal').click(); // Close the modal
productsPage.addSecondProductToCart('Stylish Dress');
// Proceed to checkout
homePage.clickCartButton();
cy.url().should('include', '/view_cart');
cy.contains('Shopping Cart').should('be.visible');
checkoutPage.clickProceedToCheckout();
// Verify address and review order
checkoutPage.verifyAddressDetails(user.firstName, user.lastName, user.company, user.address1, user.address2, user.city, user.state, user.zipCode, user.mobileNumber);
checkoutPage.verifyReviewOrder();
// Place order
checkoutPage.enterComment('This is a test order comment.');
checkoutPage.clickPlaceOrder();
// Enter payment details
checkoutPage.enterPaymentDetails(faker_1.faker.finance.accountName(), faker_1.faker.finance.creditCardNumber(), faker_1.faker.finance.creditCardCVV(), faker_1.faker.date.future().getMonth().toString(), faker_1.faker.date.future().getFullYear().toString());
// Confirm order
checkoutPage.clickPayAndConfirmOrder();
checkoutPage.verifyOrderPlacedSuccessMessage();
// Download invoice
cy.contains('Download Invoice', { timeout: 10000 })
    .should('be.visible')
    .then(function ($el) {
    var invoiceUrl = $el.attr('href');
    cy.downloadFile("http://automationexercise.com".concat(invoiceUrl), 'cypress/downloads', 'invoice.txt');
});
// Verify invoice file
cy.readFile('cypress/downloads/invoice.txt').should('exist').and('not.be.empty');
// Continue and delete account
checkoutPage.clickContinueButton();
homePage.clickDeleteAccountButton();
registrationPage.verifyAccountDeleted();
registrationPage.clickContinueButton();
